// Extract message — FULL REPLACE v3 (adds audio detection + hasAudio)
// Input: $json.body.updates[0]
// Output: { text, chatId, login, hasFile, hasAudio, files[] }

function normalizeText(s) {
  return String(s || '').replace(/\u00A0/g, ' ').trim();
}

function toArrayMaybe(x) {
  if (!x) return [];
  if (Array.isArray(x)) return x;
  return [x];
}

function uniqPushFile(arr, f) {
  const kind = String(f.kind || '');
  const id = String(f.file_id || f.id || '');
  const key = `${kind}|${id}`;
  if (!id) return;
  if (arr._seen && arr._seen.has(key)) return;
  arr._seen = arr._seen || new Set();
  arr._seen.add(key);
  arr.push(f);
}

function guessKindByName(name) {
  const n = String(name || '').toLowerCase();
  if (/\.(png|jpg|jpeg|bmp|gif|tif|tiff|webp)$/i.test(n)) return 'image';
  if (/\.(ogg|opus|mp3|wav|m4a|aac|flac)$/i.test(n)) return 'audio';
  return 'file';
}

const update = $json?.body?.updates?.[0];
if (!update) return [];

const text = normalizeText(update.text || '') || 'Empty';

// chatId/login
const chatId = update?.from?.id ?? update?.chat?.id ?? null;
const login = update?.from?.login ?? null;

const files = [];

// images can come in different shapes: images[0] array, images array, or object
const imagesRaw = update.images;
if (imagesRaw) {
  // sometimes images is [[...]] or [...]
  const imgs = Array.isArray(imagesRaw) && Array.isArray(imagesRaw[0]) ? imagesRaw[0] : imagesRaw;
  for (const src of toArrayMaybe(imgs)) {
    if (!src) continue;
    const file_id = src.file_id || src.id;
    uniqPushFile(files, {
      kind: 'image',
      file_id,
      name: src.name || '',
      size: src.size,
      width: src.width,
      height: src.height,
    });
  }
}

// generic file (docs, logs, voice, etc)
if (update.file) {
  const f = update.file;
  const file_id = f.file_id || f.id;
  const name = f.name || '';
  const kind = guessKindByName(name);
  uniqPushFile(files, {
    kind,
    file_id,
    name,
    size: f.size,
  });
}

const hasFile = files.length > 0;
const hasAudio = files.some(f => String(f.kind).toLowerCase() === 'audio');

return [{
  json: {
    text,
    chatId,
    login,
    hasFile,
    hasAudio,
    files,
  }
}];

// Code node "Stop"
// Ничего не делаем и останавливаем цепочку для этих сообщений
return [];

// Prepare files for OCR — FULL REPLACE v3 (force ORIGINAL file_id, no leader logic)
// Input: $json.files may be array OR JSON-string
// Output: preserves base, sets file_id/file_name and _ocrChosen debug

function parseFiles(v) {
  if (!v) return [];
  if (Array.isArray(v)) return v;
  if (typeof v === 'string') {
    try { const a = JSON.parse(v); return Array.isArray(a) ? a : []; } catch { return []; }
  }
  return [];
}

function isImage(f) {
  if (!f || typeof f !== 'object') return false;
  const kind = String(f.kind || '').toLowerCase();
  if (kind === 'image') return true;
  const name = String(f.name || '').toLowerCase();
  return /\.(png|jpg|jpeg|bmp|gif|tif|tiff|webp)$/i.test(name);
}

const base = $input.item.json || {};
const files = parseFiles(base.files).filter(isImage);

if (!files.length) {
  return [{
    json: {
      ...base,
      _skipOcr: true,
      file_id: '',
      file_name: '',
      _ocrChosen: null,
    }
  }];
}

// Prefer ORIGINAL: file_id without "?size="
const originals = files.filter(f => String(f.file_id || f.id || '').includes('?size=') === false);
let cand = null;

function area(f) {
  const w = Number(f.width || 0);
  const h = Number(f.height || 0);
  return (isFinite(w) ? w : 0) * (isFinite(h) ? h : 0);
}

const pool = originals.length ? originals : files;

// Pick max area, fallback last
pool.sort((a,b) => area(b) - area(a));
cand = pool[0] || pool[pool.length - 1];

const fid = String(cand.file_id || cand.id || '').trim();
const fname = (String(cand.name || '').trim()) || 'image.png';

return [{
  json: {
    ...base,
    _skipOcr: false,
    file_id: fid,
    file_name: fname,
    file_kind: 'image',
    _ocrChosen: {
      file_id: fid,
      name: fname,
      width: cand.width || null,
      height: cand.height || null,
      originalPreferred: originals.length > 0,
    }
  }
}];

// Merge OCR results — FULL REPLACE v4 (KEEP BASE FIELDS)
// Goal: preserve chatId/login/file_id/file_name/etc downstream.
// Input: items from "OCR Request" (each has ...base + ok/text/ocr_text)
// Output: single item: { ...base, ok, ocr_text, files }

const inItems = $input.all();
if (!inItems.length) {
  return [{ json: { ok: false, ocr_text: '', files: [], note: '[ocr] no results' } }];
}

// base from first item (contains chatId/login + chosen file_id/file_name, etc.)
const base = (inItems[0] && inItems[0].json) ? inItems[0].json : {};

function pickText(res) {
  return String((res && (res.ocr_text ?? res.text ?? '')) || '').trim();
}
function pickOk(res) {
  return res && res.ok === true;
}
function pickErr(res) {
  return String((res && (res.error_data || res.error || '')) || '').trim();
}

let allOk = true;
const parts = [];
const files = [];

for (const it of inItems) {
  const res = it.json || {};
  const name = String(res.file_name || res.file_id || res.id || '(no-name)').trim() || '(no-name)';
  const text = pickText(res);
  const ok = pickOk(res) && text.length > 0;
  const err = pickErr(res);

  if (!ok) allOk = false;

  files.push({
    name,
    ok,
    text,
    err,
    debug: res.ocr_debug || null,
  });

  if (text) parts.push(`Файл "${name}":\n${text}`);
  else if (err) parts.push(`Файл "${name}":\n[OCR ERROR]\n${err}`);
  else parts.push(`Файл "${name}":\n[OCR] Empty result (text not recognized).`);
}

return [{
  json: {
    ...base,
    ok: allOk,
    ocr_text: parts.join('\n\n'),
    files,
  }
}];

// OCR Request — FULL REPLACE v8.0 (NO process, host.docker.internal, 30min timeout)
// Input: base.file_id + base.file_name (from "Prepare files for OCR")
// Output: ...base + ok + ocr_text + ocr_debug
// Env: YANDEX_BOT_TOKEN_CHITALKA

const https = require('https');

const base = $input.item.json || {};
const fileId = String(base.file_id || '').trim();
const fileName = String(base.file_name || 'image.png').trim() || 'image.png';

const TIMEOUT_MS = 1800000; // 30 minutes
const SIDECAR_URL = 'http://host.docker.internal:8097/ocr/extract';

function httpRequestBuffer({ method, hostname, path, headers }, bodyBuf) {
  return new Promise((resolve, reject) => {
    const req = https.request({ method, hostname, path, headers }, (res) => {
      const chunks = [];
      res.on('data', (d) => chunks.push(Buffer.isBuffer(d) ? d : Buffer.from(d)));
      res.on('end', () => resolve({ statusCode: res.statusCode || 0, headers: res.headers || {}, buf: Buffer.concat(chunks) }));
    });
    req.on('error', reject);
    if (bodyBuf && bodyBuf.length) req.write(bodyBuf);
    req.end();
  });
}

function looksLikeJson(headers, buf) {
  const ct = String((headers && headers['content-type']) || '').toLowerCase();
  if (ct.includes('application/json')) return true;
  const b0 = (buf && buf.slice && buf.slice(0,1).toString('utf8')) || '';
  return b0 === '{' || b0 === '[';
}

function extractYandexJsonError(buf) {
  try {
    const obj = JSON.parse(buf.toString('utf8'));
    if (obj && obj.ok === false) return obj.description || obj.code || 'getFile failed';
  } catch {}
  return null;
}

async function yandexGetFileBuffer(fid) {
  const token = String($env.YANDEX_BOT_TOKEN_CHITALKA || '').trim();
  if (!token) throw new Error('YANDEX_BOT_TOKEN_CHITALKA is not set');
  if (!fid) throw new Error('file_id is empty');

  const body = Buffer.from(JSON.stringify({ file_id: fid }), 'utf8');

  const r1 = await httpRequestBuffer(
    {
      method: 'POST',
      hostname: 'botapi.messenger.yandex.net',
      path: '/bot/v1/messages/getFile/',
      headers: {
        'Authorization': `OAuth ${token}`,
        'Content-Type': 'application/json',
        'Content-Length': String(body.length),
      },
    },
    body
  );

  if (looksLikeJson(r1.headers, r1.buf)) {
    const err = extractYandexJsonError(r1.buf);
    if (err) throw new Error(err);
  }
  if (r1.statusCode >= 200 && r1.statusCode < 300) return r1.buf;

  const p = `/bot/v1/messages/getFile/?file_id=${encodeURIComponent(fid)}`;
  const r2 = await httpRequestBuffer(
    { method: 'GET', hostname: 'botapi.messenger.yandex.net', path: p, headers: { 'Authorization': `OAuth ${token}` } },
    null
  );

  if (looksLikeJson(r2.headers, r2.buf)) {
    const err = extractYandexJsonError(r2.buf);
    if (err) throw new Error(err);
  }
  if (r2.statusCode >= 200 && r2.statusCode < 300) return r2.buf;

  throw new Error(`getFile failed: POST=${r1.statusCode} | GET=${r2.statusCode}`);
}

if (!fileId) {
  return [{ json: { ...base, ok: false, ocr_text: '', text: '', ocr_debug: { note: 'file_id missing' } } }];
}

// download image bytes
let imgBuf;
try {
  imgBuf = await yandexGetFileBuffer(fileId);
} catch (e) {
  return [{ json: { ...base, ok: false, ocr_text: '', text: '', ocr_debug: { note: `download failed: ${e.message || e}` } } }];
}

const lang = (base.forceOcrEn === true) ? 'en' : 'ru';
const image_b64 = imgBuf.toString('base64');

// call sidecar OCR
try {
  const resp = await this.helpers.httpRequest({
    method: 'POST',
    url: SIDECAR_URL,
    json: true,
    timeout: TIMEOUT_MS,
    body: { image_b64, file_name: fileName, lang, debug: true },
  });

  const text = String(resp?.text || '').trim();

  // IMPORTANT: не ставим base.error/base.error_data — чтобы VLM продолжил даже если OCR пустой
  return [{
    json: {
      ...base,
      ok: !!text,
      ocr_text: text,
      text,
      ocr_debug: { url: SIDECAR_URL, lang, timeout_ms: TIMEOUT_MS, tries: resp?.tries || [], image_info: resp?.image_info || null },
    }
  }];

} catch (e) {
  // тоже не “ломаем” пайплайн ошибкой — просто отдаём пустой OCR и debug
  const status = e?.response?.status ?? e?.statusCode ?? '';
  let dataStr = '';
  try { dataStr = e?.response?.data ? JSON.stringify(e.response.data) : ''; } catch { dataStr = String(e?.response?.data || ''); }

  return [{
    json: {
      ...base,
      ok: false,
      ocr_text: '',
      text: '',
      ocr_debug: { url: SIDECAR_URL, lang, timeout_ms: TIMEOUT_MS, error: `status=${status} ${e?.message || e}`, error_data: dataStr.slice(0, 2000) }
    }
  }];
}

// Chitalka Extract PDF/TXT — FULL REPLACE v7.3 (token-based, dpi=200 quality=60 defaults, never-throw)
// Sidecar: http://host.docker.internal:8097/pdf/textlayer + /pdf/pages_meta
// Timeout: 30 minutes
// Env: YANDEX_BOT_TOKEN_CHITALKA
// Optional env overrides: CHITALKA_PDF_DPI (default 200), CHITALKA_PDF_QUALITY (default 60)

const https = require('https');

const base = $input.item.json || {};
const TIMEOUT_MS = 1800000;
const SIDECAR = 'http://host.docker.internal:8097';

function s(x){ return x == null ? '' : String(x); }

function clampInt(v, lo, hi, def) {
  let n = Number(v);
  if (!Number.isFinite(n)) n = def;
  n = Math.trunc(n);
  if (n < lo) n = lo;
  if (n > hi) n = hi;
  return n;
}

const DPI = clampInt($env.CHITALKA_PDF_DPI, 72, 350, 200);
const QUALITY = clampInt($env.CHITALKA_PDF_QUALITY, 30, 95, 60);

function parseFiles(v) {
  if (!v) return [];
  if (Array.isArray(v)) return v;
  if (typeof v === 'string') { try { const a = JSON.parse(v); return Array.isArray(a) ? a : []; } catch { return []; } }
  return [];
}
function isImageFile(f) {
  const kind = String((f && f.kind) || '').toLowerCase();
  if (kind === 'image') return true;
  const name = String((f && f.name) || '').toLowerCase();
  return /\.(png|jpg|jpeg|bmp|gif|tif|tiff|webp)$/i.test(name);
}
function isAudioFile(f) {
  const kind = String((f && f.kind) || '').toLowerCase();
  if (kind === 'audio') return true;
  const name = String((f && f.name) || '').toLowerCase();
  return /\.(ogg|opus|mp3|wav|m4a|aac|flac)$/i.test(name);
}
function pickPdf(filesArr) {
  const non = filesArr.filter(f => f && !isImageFile(f) && !isAudioFile(f));
  if (!non.length) return null;
  const pdf = non.filter(f => /\.pdf$/i.test(String(f.name || '')));
  return pdf.length ? pdf[pdf.length - 1] : non[non.length - 1];
}

function httpRequestBuffer({ method, hostname, path, headers }, bodyBuf) {
  return new Promise((resolve, reject) => {
    const req = https.request({ method, hostname, path, headers: headers || {} }, (res) => {
      const chunks = [];
      res.on('data', (d) => chunks.push(Buffer.isBuffer(d) ? d : Buffer.from(d)));
      res.on('end', () => resolve({ statusCode: res.statusCode || 0, headers: res.headers || {}, buf: Buffer.concat(chunks) }));
    });
    req.on('error', reject);
    if (bodyBuf && bodyBuf.length) req.write(bodyBuf);
    req.end();
  });
}
function looksLikeJson(headers, buf) {
  const ct = String((headers && headers['content-type']) || '').toLowerCase();
  if (ct.includes('application/json')) return true;
  const b0 = (buf && buf.slice && buf.slice(0,1).toString('utf8')) || '';
  return b0 === '{' || b0 === '[';
}
function extractYandexJsonError(buf) {
  try {
    const obj = JSON.parse(buf.toString('utf8'));
    if (obj && obj.ok === false) return obj.description || obj.code || 'getFile failed';
  } catch {}
  return null;
}

async function yandexGetFileBuffer(fid) {
  const token = String($env.YANDEX_BOT_TOKEN_CHITALKA || '').trim();
  if (!token) throw new Error('YANDEX_BOT_TOKEN_CHITALKA is not set');
  if (!fid) throw new Error('file_id is empty');

  const body = Buffer.from(JSON.stringify({ file_id: fid }), 'utf8');

  const r1 = await httpRequestBuffer(
    {
      method: 'POST',
      hostname: 'botapi.messenger.yandex.net',
      path: '/bot/v1/messages/getFile/',
      headers: {
        'Authorization': `OAuth ${token}`,
        'Content-Type': 'application/json',
        'Content-Length': String(body.length),
      },
    },
    body
  );

  if (looksLikeJson(r1.headers, r1.buf)) {
    const err = extractYandexJsonError(r1.buf);
    if (err) throw new Error(err);
  }
  if (r1.statusCode >= 200 && r1.statusCode < 300) return r1.buf;

  const p = `/bot/v1/messages/getFile/?file_id=${encodeURIComponent(fid)}`;
  const r2 = await httpRequestBuffer(
    { method: 'GET', hostname: 'botapi.messenger.yandex.net', path: p, headers: { 'Authorization': `OAuth ${token}` } },
    null
  );

  if (looksLikeJson(r2.headers, r2.buf)) {
    const err = extractYandexJsonError(r2.buf);
    if (err) throw new Error(err);
  }
  if (r2.statusCode >= 200 && r2.statusCode < 300) return r2.buf;

  throw new Error(`getFile failed: POST=${r1.statusCode} | GET=${r2.statusCode}`);
}

function statusFromErr(e) {
  const st = e?.response?.status ?? e?.statusCode ?? null;
  if (st != null) return String(st);
  const m = String(e?.message || '').match(/status code\s*(\d+)/i);
  return m ? m[1] : '';
}
function errDataToString(e) {
  try {
    const d = e?.response?.data;
    if (d == null) return '';
    if (Buffer.isBuffer(d)) return d.toString('utf8').slice(0, 3000);
    if (typeof d === 'string') return d.slice(0, 3000);
    return JSON.stringify(d).slice(0, 3000);
  } catch { return ''; }
}

// never throw; returns {ok:boolean, data:any, err?:string}
async function safeHttp(req) {
  try {
    const data = await this.helpers.httpRequest(req);
    return { ok: true, data };
  } catch (e) {
    const st = statusFromErr(e);
    const body = errDataToString(e) || '(no response body)';
    return { ok: false, err: `http failed status=${st} ${String(e?.message || e)}`, data: body };
  }
}

// ---- MAIN ----
const files = parseFiles(base.files);
const pdf = pickPdf(files);
if (!pdf) return [{ json: { ...base, _chitalka_no_doc: true, error: 'PDF не найден во вложениях.' } }];

const fid = s(pdf.file_id || pdf.id).trim();
const fname = s(pdf.name || 'document.pdf').trim() || 'document.pdf';

let pdfBuf;
try { pdfBuf = await yandexGetFileBuffer(fid); }
catch (e) { return [{ json: { ...base, _chitalka_no_doc: true, error: `Не смог скачать PDF: ${e.message || e}` } }]; }

const pdf_b64 = pdfBuf.toString('base64');

// 1) textlayer
const tlRes = await safeHttp.call(this, {
  method: 'POST',
  url: `${SIDECAR}/pdf/textlayer`,
  json: true,
  timeout: TIMEOUT_MS,
  body: { pdf_b64, dpi: DPI }
});

if (!tlRes.ok) {
  return [{
    json: {
      ...base,
      _chitalka_no_doc: true,
      error: 'PDF textlayer check failed',
      error_data: JSON.stringify(tlRes, null, 2).slice(0, 3000)
    }
  }];
}

const tl = tlRes.data || {};
const score = Number(tl.score || 0);
const tlText = String(tl.text || '').trim();

if (score >= 300 && tlText) {
  return [{
    json: {
      ...base,
      source_file_name: fname,
      pdf_mode: 'textlayer',
      ocr_text: tlText,
      pdf_text_score: score,
      pdf_dpi: DPI
    }
  }];
}

// 2) pages_meta
const metaRes = await safeHttp.call(this, {
  method: 'POST',
  url: `${SIDECAR}/pdf/pages_meta`,
  json: true,
  timeout: TIMEOUT_MS,
  body: { pdf_b64, dpi: DPI, fmt: 'jpg', quality: QUALITY }
});

if (!metaRes.ok) {
  return [{
    json: {
      ...base,
      _chitalka_no_doc: true,
      error: 'PDF pages_meta failed',
      error_data: JSON.stringify(metaRes, null, 2).slice(0, 3000)
    }
  }];
}

const meta = metaRes.data || {};
if (!meta.ok) {
  return [{
    json: {
      ...base,
      _chitalka_no_doc: true,
      error: 'PDF render failed',
      error_data: JSON.stringify(meta).slice(0, 3000)
    }
  }];
}

return [{
  json: {
    ...base,
    source_file_name: fname,
    pdf_mode: 'scanned',
    pdf_token: String(meta.token || ''),
    pdf_page_count: Number(meta.page_count || 0),
    pdf_dpi: Number(meta.dpi || DPI),
    pdf_fmt: String(meta.fmt || 'jpg'),
    pdf_quality: Number(meta.quality || QUALITY),
  }
}];

// Chitalka Build Output — FULL REPLACE v3.0
// Fixes: renders vlm_blocks even if they arrived as JSON-string; if vlm_blocks missing but vlm_text is JSON -> parses it.
// Normalizes tables: trims empty/all-null columns, fixes cols, header_rows, centers numeric grids.
// Adds colgroup (equal widths) for stable table layout in Word HTML.
// Supports: header_line, paragraph, table, page_break.
// Underline: __...__ -> <u>...</u>.

function s(x){ return x == null ? '' : String(x); }

function toSafeBaseName(s0, fallback) {
  const raw = (s0 || '').toString().trim();
  const base = raw.slice(0, 80) || fallback;
  return (base
    .replace(/[\\/:*?"<>|]+/g, ' ')
    .replace(/\s+/g, ' ')
    .trim()
    .slice(0, 80)) || fallback;
}

function escapeHtml(text){
  return String(text||'')
    .replace(/&/g,'&amp;')
    .replace(/</g,'&lt;')
    .replace(/>/g,'&gt;')
    .replace(/"/g,'&quot;');
}

function escapeHtmlWithUnderlineMarkup(text) {
  const src = String(text || '');
  const parts = src.split(/(__)/);
  let under = false;
  let out = '';
  for (const p of parts) {
    if (p === '__') { under = !under; out += under ? '<u>' : '</u>'; continue; }
    out += escapeHtml(p);
  }
  if (under) out += '</u>';
  out = out.replace(/\n/g, '<br/>');
  return out;
}

function stripPageMarkers(text) {
  return String(text || '').replace(/^\s*---\s*PAGE\s+\d+\s*---\s*$/gmi, '').trim();
}

function formatErr(base){
  const parts = [];
  parts.push('Chitalka: не удалось обработать документ.');
  if (base.error) parts.push(`Error: ${s(base.error).slice(0, 900)}`);
  if (base.error_data) parts.push(`Details: ${s(base.error_data).slice(0, 1400)}`);
  if (base._pdf_debug) parts.push(`PDF debug: ${JSON.stringify(base._pdf_debug).slice(0, 1400)}`);
  if (base.ocr_debug) parts.push(`OCR debug: ${JSON.stringify(base.ocr_debug).slice(0, 1400)}`);
  if (base.vlm_debug) parts.push(`VLM debug: ${JSON.stringify(base.vlm_debug).slice(0, 1400)}`);
  return parts.join('\n');
}

function cssForStyle(style) {
  const st = String(style || 'body').toLowerCase();
  if (st === 'title') return 'font-size:20pt; font-weight:bold;';
  if (st === 'header') return 'font-size:14pt; font-weight:bold;';
  if (st === 'small') return 'font-size:9pt;';
  return 'font-size:11pt;';
}

function isJsonLike(x) {
  const t = String(x || '').trim();
  if (!t) return false;
  const y = t.replace(/^```(?:json)?\s*/i, '').replace(/```$/i, '').trim();
  return (y.startsWith('{') || y.startsWith('[')) && y.includes('"type"') && (y.includes('"rows"') || y.includes('"blocks"'));
}

function parseMaybeJson(x) {
  try {
    const t = String(x || '').trim()
      .replace(/^```(?:json)?\s*/i, '')
      .replace(/```$/i, '')
      .trim();
    if (!t) return null;
    return JSON.parse(t);
  } catch {
    return null;
  }
}

function isCellObj(c) {
  return c && typeof c === 'object' && !Array.isArray(c) && ('text' in c || 'rowspan' in c || 'colspan' in c);
}

function emptyCell(center=false) {
  return {
    text: '',
    rowspan: 1,
    colspan: 1,
    align: center ? 'center' : 'left',
    valign: 'middle',
    bold: false,
    italic: false,
  };
}

function isMostlyNumericGrid(rows) {
  let total = 0, num = 0;
  for (const r of rows) {
    if (!Array.isArray(r)) continue;
    for (const c of r) {
      if (c === null) continue;
      if (!isCellObj(c)) continue;
      const tx = String(c.text || '').trim();
      if (!tx) { total++; continue; }
      total++;
      if (/^\d{1,4}$/.test(tx)) num++;
    }
  }
  if (total < 20) return false;
  return (num / total) >= 0.75;
}

function trimAllNullColumns(rows) {
  if (!rows.length) return rows;

  let maxLen = 0;
  for (const r of rows) maxLen = Math.max(maxLen, Array.isArray(r) ? r.length : 0);

  const padded = rows.map(r => {
    const rr = Array.isArray(r) ? r.slice() : [];
    while (rr.length < maxLen) rr.push(null);
    return rr;
  });

  let right = maxLen - 1;
  while (right >= 0) {
    let allNull = true;
    for (const r of padded) {
      if (r[right] !== null) { allNull = false; break; }
    }
    if (!allNull) break;
    right--;
  }

  let left = 0;
  while (left <= right) {
    let allNull = true;
    for (const r of padded) {
      if (r[left] !== null) { allNull = false; break; }
    }
    if (!allNull) break;
    left++;
  }

  const out = padded.map(r => r.slice(left, right + 1));
  return out;
}

function normalizeTable(t) {
  const rows0 = Array.isArray(t?.rows) ? t.rows : [];
  if (!rows0.length) return t;

  let rows = trimAllNullColumns(rows0);

  let cols = 0;
  for (const r of rows) cols = Math.max(cols, Array.isArray(r) ? r.length : 0);

  const numericGrid = isMostlyNumericGrid(rows);
  rows = rows.map(r => {
    const rr = Array.isArray(r) ? r.slice() : [];
    while (rr.length < cols) rr.push(emptyCell(numericGrid));
    for (let i = 0; i < rr.length; i++) {
      const c = rr[i];
      if (c === null) continue;
      if (!isCellObj(c)) rr[i] = emptyCell(numericGrid);
      rr[i].rowspan = Number(rr[i].rowspan || 1) || 1;
      rr[i].colspan = Number(rr[i].colspan || 1) || 1;
      rr[i].valign = String(rr[i].valign || 'middle');
      rr[i].bold = rr[i].bold === true;
      rr[i].italic = rr[i].italic === true;
      if (numericGrid) rr[i].align = 'center';
      else rr[i].align = String(rr[i].align || 'left');
    }
    return rr;
  });

  let headerRows = Number(t?.header_rows || 0) || 0;
  if (numericGrid) headerRows = 0;

  return {
    ...t,
    type: 'table',
    cols,
    header_rows: headerRows,
    rows,
  };
}

function normalizeBlocks(root) {
  if (!root || typeof root !== 'object') return root;

  if (root.type === 'table') {
    return normalizeTable(root);
  }

  if (root.type === 'blocks' && Array.isArray(root.blocks)) {
    const blocks = root.blocks.map(b => {
      if (!b || typeof b !== 'object') return b;
      if (b.type === 'table') return normalizeTable(b);
      return b;
    });
    return { ...root, blocks };
  }

  return root;
}

function renderHeaderLine(left, right, style) {
  const L = escapeHtmlWithUnderlineMarkup(stripPageMarkers(left || ''));
  const R = escapeHtmlWithUnderlineMarkup(stripPageMarkers(right || ''));
  const css = cssForStyle(style || 'header');
  return `
<table style="width:100%; border-collapse:collapse; margin:0 0 6px 0; ${css}">
  <tr>
    <td style="width:70%; vertical-align:top; padding:0;">${L}</td>
    <td style="width:30%; vertical-align:top; padding:0; text-align:right;">${R}</td>
  </tr>
</table>`;
}

function renderTable(tableObj) {
  const rows = Array.isArray(tableObj?.rows) ? tableObj.rows : [];
  const headerRows = Number(tableObj?.header_rows || 0) || 0;
  const cols = Number(tableObj?.cols || 0) || (rows[0]?.length || 0);
  if (!rows.length || !cols) return '';

  const tableCss = `border-collapse:collapse; width:100%; table-layout:fixed; ${cssForStyle(tableObj?.style || 'body')}`;
  const cellBase = `border:1px solid #000; padding:6px; vertical-align:middle;`;

  let colgroup = '<colgroup>';
  const w = (100 / cols).toFixed(4);
  for (let i = 0; i < cols; i++) colgroup += `<col style="width:${w}%;">`;
  colgroup += '</colgroup>';

  let html = `<table style="${tableCss}">${colgroup}`;
  for (let r = 0; r < rows.length; r++) {
    const row = Array.isArray(rows[r]) ? rows[r] : [];
    html += '<tr>';
    for (let c = 0; c < row.length; c++) {
      const cell = row[c];
      if (cell === null) continue;

      const text = s(cell?.text || '');
      const rs = Number(cell?.rowspan || 1) || 1;
      const cs = Number(cell?.colspan || 1) || 1;
      const align = s(cell?.align || 'left').toLowerCase();
      const valign = s(cell?.valign || 'middle').toLowerCase();
      const bold = cell?.bold === true;
      const italic = cell?.italic === true;
      const styleCss = cssForStyle(cell?.style || tableObj?.style || 'body');

      const tag = (r < headerRows) ? 'th' : 'td';
      const style =
        `${cellBase}` +
        `${styleCss}` +
        `text-align:${(align==='center'||align==='right')?align:'left'};` +
        `vertical-align:${(valign==='top'||valign==='bottom')?valign:'middle'};` +
        (bold ? 'font-weight:bold;' : '') +
        (italic ? 'font-style:italic;' : '');

      const inner = text ? escapeHtmlWithUnderlineMarkup(text) : '&nbsp;';
      const attrs =
        (rs > 1 ? ` rowspan="${rs}"` : '') +
        (cs > 1 ? ` colspan="${cs}"` : '') +
        ` style="${style}"`;

      html += `<${tag}${attrs}>${inner}</${tag}>`;
    }
    html += '</tr>';
  }
  html += '</table>';
  return html;
}

function renderParagraph(p) {
  const t = stripPageMarkers(p?.text || '');
  if (!String(t).trim()) return '';
  const align = s(p?.align || 'left').toLowerCase();
  const css = cssForStyle(p?.style || 'body');
  const bold = p?.bold === true;
  const italic = p?.italic === true;
  const alignCss = (align === 'center' || align === 'right' || align === 'justify') ? align : 'left';
  const extra = (bold ? 'font-weight:bold;' : '') + (italic ? 'font-style:italic;' : '');
  return `<div style="margin:0 0 10px 0; text-align:${alignCss}; ${css} ${extra}">${escapeHtmlWithUnderlineMarkup(t)}</div>`;
}

function renderBlocks(blocksRoot) {
  if (!blocksRoot) return '';
  const blocks = [];
  if (blocksRoot.type === 'table') blocks.push(blocksRoot);
  else if (blocksRoot.type === 'blocks' && Array.isArray(blocksRoot.blocks)) blocks.push(...blocksRoot.blocks);
  else return '';

  let html = '';
  for (const b of blocks) {
    if (!b) continue;

    if (b.type === 'page_break') {
      html += `<div style="page-break-after:always;"></div>`;
      continue;
    }
    if (b.type === 'header_line') {
      html += renderHeaderLine(b.left || '', b.right || '', b.style || 'header');
      continue;
    }
    if (b.type === 'paragraph') {
      html += renderParagraph(b);
      continue;
    }
    if (b.type === 'table') {
      html += `<div style="margin:10px 0;">${renderTable(b)}</div>`;
      continue;
    }
  }
  return html;
}

function splitTabsPreserve(line) {
  return String(line || '').split(/\t/);
}

function alignForText(txt) {
  const t = String(txt || '').trim();
  if (!t) return 'left';
  if (/^[\d\s.,]+$/.test(t)) return 'right';
  return 'left';
}

function collapseParagraphLines(lines) {
  const out = [];
  let current = '';
  for (const line of lines) {
    const trimmed = String(line || '').trim();
    if (!trimmed) continue;
    if (!current) {
      current = trimmed;
      continue;
    }
    if (/[A-Za-zА-Яа-яЁё]-$/.test(current) && /^[A-Za-zА-Яа-яЁё]/.test(trimmed)) {
      current = current.replace(/-$/, '') + trimmed;
      continue;
    }
    current += ' ' + trimmed;
  }
  if (current) out.push(current);
  return out.join(' ').replace(/\s+/g, ' ').trim();
}

function plainTextToBlocks(text) {
  const src = stripPageMarkers(String(text || '')).replace(/\r\n/g, '\n');
  if (!src.trim()) return [];
  const lines = src.split('\n');
  const blocks = [];
  let paraLines = [];
  let tableLines = [];
  const bulletRe = /^\s*([•●◦\-–—]|\d+[.)])\s+/;

  function flushParagraph() {
    if (!paraLines.length) return;
    const txt = collapseParagraphLines(paraLines);
    if (txt) blocks.push({ type: 'paragraph', text: txt, align: 'left' });
    paraLines = [];
  }

  function flushTable() {
    if (!tableLines.length) return;
    const rows = tableLines.map(line => {
      const cells = splitTabsPreserve(line).map(cell => ({
        text: String(cell || '').trim(),
        rowspan: 1,
        colspan: 1,
        align: alignForText(cell),
        valign: 'middle',
        bold: false,
        italic: false,
      }));
      return cells;
    });
    const cols = rows.reduce((m, r) => Math.max(m, r.length), 0);
    for (const r of rows) {
      while (r.length < cols) r.push(emptyCell());
    }
    const table = normalizeTable({ type: 'table', cols, header_rows: 0, rows });
    blocks.push(table);
    tableLines = [];
  }

  for (const line of lines) {
    const trimmed = String(line || '').trim();
    if (!trimmed) {
      flushParagraph();
      flushTable();
      continue;
    }
    if (trimmed.includes('\t')) {
      flushParagraph();
      tableLines.push(line);
      continue;
    }
    if (tableLines.length) {
      flushTable();
    }
    if (bulletRe.test(trimmed)) {
      flushParagraph();
      blocks.push({ type: 'paragraph', text: trimmed, align: 'left' });
      continue;
    }
    paraLines.push(trimmed);
  }

  flushParagraph();
  flushTable();
  return blocks;
}

