// VLM PDF Normalize — FULL REPLACE v5.0 (JSON blocks for layout, 30min timeout)
// Cleanup token always.

const TIMEOUT_MS = 1800000;
const SIDECAR = 'http://host.docker.internal:8097';
const OLLAMA_URL = String($env.OLLAMA_URL || 'http://host.docker.internal:11434').replace(/\/$/, '');
const VLM_MODEL = 'qwen2.5vl:72b-q8_0';

const base = $input.item.json || {};
function s(x){ return x == null ? '' : String(x); }

function normalizeText(t) {
  let x = String(t || '').replace(/\r\n/g, '\n');
  x = x.replace(/^\s*---\s*PAGE\s+\d+\s*---\s*$/gmi, '');
  x = x.replace(/\n{4,}/g, '\n\n\n');
  return x.trim();
}

function stripCodeFences(t) {
  let x = String(t || '').trim();
  x = x.replace(/^```(?:json)?\s*/i, '').replace(/```$/i, '').trim();
  return x;
}
function tryParseJson(x) {
  try { return JSON.parse(x); } catch { return null; }
}

function statusFromErr(e) {
  const st = e?.response?.status ?? e?.statusCode ?? null;
  if (st != null) return String(st);
  const m = String(e?.message || '').match(/status code\s*(\d+)/i);
  return m ? m[1] : '';
}
function errDataToString(e) {
  try {
    const d = e?.response?.data;
    if (d == null) return '';
    if (typeof d === 'string') return d.slice(0, 2000);
    return JSON.stringify(d).slice(0, 2000);
  } catch { return ''; }
}

async function safeHttp(req) {
  try {
    const data = await this.helpers.httpRequest(req);
    return { ok: true, data };
  } catch (e) {
    const st = statusFromErr(e);
    const body = errDataToString(e) || '(no response body)';
    return { ok: false, err: `http failed status=${st} ${String(e?.message || e)}`, data: body, status: st };
  }
}

async function cleanupToken(token) {
  await safeHttp.call(this, {
    method: 'POST',
    url: `${SIDECAR}/pdf/cleanup`,
    json: true,
    timeout: TIMEOUT_MS,
    body: { token },
  });
}

function sleep(ms){ return new Promise(r => setTimeout(r, ms)); }

async function getPageB64(token, page) {
  const r = await safeHttp.call(this, {
    method: 'GET',
    url: `${SIDECAR}/pdf/page_b64?token=${encodeURIComponent(token)}&page=${page}`,
    json: true,
    timeout: TIMEOUT_MS,
  });
  if (!r.ok) throw new Error(`page_b64_http_failed ${r.err} body=${String(r.data).slice(0, 500)}`);
  if (!r.data?.ok) throw new Error(`page_b64_bad ${JSON.stringify(r.data).slice(0, 800)}`);
  return String(r.data.b64 || '');
}

function looksLikeTableHint(hint) {
  const h = String(hint || '');
  if ((h.match(/\t/g) || []).length >= 6) return true;
  const lines = h.split('\n').map(v => v.trim()).filter(Boolean);
  let score = 0;
  for (const ln of lines.slice(0, 40)) {
    const nums = (ln.match(/\b\d+\b/g) || []).length;
    if (nums >= 6) score++;
  }
  if (score >= 2) return true;
  if (/(№|наименован|кол-во|цена|стоим|итого|руб)/i.test(h)) return true;
  return false;
}

async function vlmBlocksFromImage(imageB64, ocrHint, preferTable) {
  const schema =
`OUTPUT MUST BE VALID JSON ONLY. NO MARKDOWN, NO TEXT OUTSIDE JSON.

Return one of:
A) {"type":"table","cols":N,"header_rows":H,"rows":[[cellOrNull,...],...]}
B) {"type":"blocks","blocks":[block,...]}

block:
- {"type":"paragraph","text":"...","align":"left|center|right|justify"}  // keep real alignment
- {"type":"table","cols":N,"header_rows":H,"rows":[[cellOrNull,...],...]}
- {"type":"page_break"}

cellOrNull:
- null  // covered by a previous cell with colspan/rowspan
- {"text":"", "rowspan":1, "colspan":1, "align":"left|center|right", "valign":"top|middle|bottom", "bold":false, "italic":false}

RULES (CRITICAL):
- Preserve EMPTY CELLS: represent them as {"text":"","rowspan":1,"colspan":1,...}
- Preserve MERGED CELLS: first cell has colspan/rowspan; covered positions must be null.
- Preserve alignment: numbers usually right, quantities center, text left.
- Preserve paragraph alignment (left/center/right/justify).
- Do NOT invent content. If unreadable -> "".
- Do NOT output page numbers or page markers.
- For underlined fragments: wrap with __double_underscores__.
- For stress accents in RU: use combining acute U+0301 after vowel if visible.
`;

  const modeHint = preferTable
    ? `The image is VERY LIKELY a TABLE. Prefer output type "table" (or blocks with a table).`
    : `The image may be a letter or a mixed page. Prefer "blocks" and include tables only if clearly present.`;

  const prompt =
`ROLE: DOCUMENT LAYOUT RECONSTRUCTOR (NOT A CHATBOT)

${modeHint}

${schema}

OCR_HINT (may be wrong, image is authoritative):
${(ocrHint || '').slice(0, 6000)}
`;

  const res = await safeHttp.call(this, {
    method: 'POST',
    url: `${OLLAMA_URL}/api/chat`,
    json: true,
    timeout: TIMEOUT_MS,
    body: {
      model: VLM_MODEL,
      stream: false,
      options: { temperature: 0, top_p: 1, top_k: 1 },
      messages: [{ role: 'user', content: prompt, images: [imageB64] }],
    }
  });

  if (!res.ok) throw new Error(`vlm_blocks_failed ${res.err} body=${String(res.data || '').slice(0, 700)}`);

  const raw = stripCodeFences(res.data?.message?.content || '');
  return { raw, parsed: tryParseJson(raw) };
}

async function vlmTextFromImage(imageB64) {
  const prompt = `ROLE: DOCUMENT TRANSCRIBER (NOT A CHATBOT)
Transcribe text from image. Preserve line breaks. Tables -> TAB separated columns.
Output ONLY text.`;

  const res = await safeHttp.call(this, {
    method: 'POST',
    url: `${OLLAMA_URL}/api/chat`,
    json: true,
    timeout: TIMEOUT_MS,
    body: {
      model: VLM_MODEL,
      stream: false,
      options: { temperature: 0, top_p: 1, top_k: 1 },
      messages: [{ role: 'user', content: prompt, images: [imageB64] }],
    }
  });

  if (!res.ok) throw new Error(`vlm_text_failed ${res.err} body=${String(res.data || '').slice(0, 700)}`);

  return String(res.data?.message?.content || '').trim()
    .replace(/^```[\w-]*\s*/i, '')
    .replace(/```$/i, '')
    .trim();
}

// ---- MAIN ----
const mode = s(base.pdf_mode).trim();

if (mode === 'textlayer') {
  return [{ json: { ...base, vlm_text: normalizeText(base.ocr_text || ''), vlm_debug: { mode: 'textlayer', model: VLM_MODEL } } }];
}

const token = s(base.pdf_token).trim();
const n = Number(base.pdf_page_count || 0);

if (!token || !n) {
  return [{ json: { ...base, _chitalka_no_doc: true, error: 'VLM PDF Normalize: token/page_count missing.' } }];
}

const blocks = [];
let errOut = null;

try {
  for (let page = 1; page <= n; page++) {
    const imgB64 = await getPageB64.call(this, token, page);
    let parsed = null;
    try {
      const hint = s(base.ocr_text || '');
      const preferTable = looksLikeTableHint(hint);
      const { parsed: p } = await vlmBlocksFromImage.call(this, imgB64, hint, preferTable);
      parsed = p;
    } catch (e) {
      parsed = null;
    }

    if (parsed && parsed.type === 'table') {
      blocks.push(parsed);
    } else if (parsed && parsed.type === 'blocks' && Array.isArray(parsed.blocks)) {
      blocks.push(...parsed.blocks);
    } else {
      const txt = await vlmTextFromImage.call(this, imgB64);
      if (txt) blocks.push({ type: 'paragraph', text: normalizeText(txt), align: 'left' });
    }

    if (page < n) blocks.push({ type: 'page_break' });
  }
} catch (e) {
  errOut = String(e?.message || e);
} finally {
  await cleanupToken.call(this, token);
}

if (errOut) {
  return [{
    json: {
      ...base,
      _chitalka_no_doc: true,
      error: 'VLM PDF Normalize failed',
      error_data: errOut.slice(0, 2000),
      vlm_debug: { mode: 'scanned_vlm', pages: n, model: VLM_MODEL }
    }
  }];
}

if (!blocks.length) {
  return [{
    json: { ...base, _chitalka_no_doc: true, error: 'VLM PDF Normalize: empty result', vlm_debug: { mode: 'scanned_vlm', pages: n, model: VLM_MODEL } }
  }];
}

return [{
  json: {
    ...base,
    vlm_blocks: { type: 'blocks', blocks },
    vlm_text: '',
    vlm_debug: { mode: 'scanned_vlm_blocks', pages: n, model: VLM_MODEL }
  }
}];

